msg = {
    url: global.get('conveyor-api').url + '/flow/{{_url}}',
    method: 'GET'
}


return msg;

var parameters = msg.payload.parameters;
var parameterNames = [];

parameters.forEach(function(param) {
    parameterNames.push(param.key)

    flow.set(param.key, JSON.parse(param.value))
})

flow.set('params', parameterNames);

return msg;

var paramNames = flow.get('params');
var parameters = {};

paramNames.forEach(function(name) {
    parameters[name] = flow.get(name);
})

msg.payload = parameters
return msg;

var paramNames = flow.get('params');
var updates = [];
var errors = [];

if (msg.payload) {
    var paramsToUpdate = Object.keys(msg.payload);

    paramsToUpdate.forEach(function(update) {
        if (paramNames.includes(update)) {
            var updateParam = {
                key: update,
                value: msg.payload[update]
            }

            updates.push(updateParam)
        } else {
            errors.push('Parameter [' + update + '] not valid.');
        }
    })
} else {
    errors.push('No Payload found');
}

if (errors.length > 0) {
    msg.payload = errors
} else {
    updates.forEach(function(update) {
        flow.set(update.key, update.value)
    })

    var elasticParamsUpdate = [];
    paramNames.forEach(function(param) {
        elasticParamsUpdate.push({
            "key": param,
            "value": flow.get(param)
        })
    })

    msg.url = global.get('node-red').url + '/elasticsearch',
    msg.method = 'POST',
    msg.payload = {};
    msg.payload.path = '/flow/default/{{_id}}/_update',
    msg.payload.method = 'POST',
    msg.payload.payload = {
        "doc" : {
        "parameters" : elasticParamsUpdate
        }
    }
}
return msg;

if (msg.statusCode != 200) {
    msg.payload = 'Local Flow Configuration Updated, but not persisted.',
    msg.statusCode = 500
}
return msg;

msg.url = global.get('node-red').url + '/elasticsearch',
