if (msg.statusCode == 200) {
    if (msg.payload.length == 100) {
        if (!flow.get('stargazerPage')) {
            flow.set('stargazerPage', 2)
        } else {
            flow.set('stargazerPage', Number(flow.get('stargazerPage')) + 1)
        }

        return [msg, {}]
    } else {
        return [msg, null]
    }
}

